      TME_FB_XLAT_SHIFT_DST(dst_fifo0_may_be_unaligned,
                            dst_fifo0,
                            dst_fifo0_next,
                            dst_fifo0_bits,
                            32,
                            dst_raw0,
                            dst_order);

